package com.stormunblessed

import com.lagradost.cloudstream3.app

class StreamedInfo {
    var mainUrl = "https://streamed.pk"
    var matches: MatchesResult = MatchesResult();

    suspend fun init() {
        val res = app.get("$mainUrl/api/matches/all-today")
        this.matches = res.parsed<MatchesResult>()
    }

    fun searchPosterByTitle(title: String): String? {
        return this.matches.find {
            title.trim().contains(it.title.trim(), true)
        }?.poster?.replaceFirst("^/".toRegex(), "$mainUrl/")
    }
}

val defaultPoster: String =
    "data:image/webp;base64,"

class MatchesResult : ArrayList<APIMatch>()
class SourceResult : ArrayList<SourceInfo>()
data class APIMatch(
    val id: String,
    val title: String,
    val category: String,
    val date: Long,
    val poster: String? = null,
    val popular: Boolean,
    val teams: Teams? = null,
    val sources: List<Source>
)

data class Teams(
    val home: TeamInfo? = null,
    val away: TeamInfo? = null
)

data class TeamInfo(
    val name: String?,
    val badge: String?
)

data class Source(
    val source: String,
    val id: String
)

data class SourceInfo(
    val id: String,
    val streamNo: Int,
    val language: String,
    val hd: Boolean,
    val embedUrl: String,
    val source: String,
    val viewers: Int
)